import React, { useState } from 'react';
import ProductTable from './ProductTable'; // Importa o componente ProductTable
import UserTable from './UserTable';

function HomeBackOffice() {
    const [showUsers, setShowUsers] = useState(false); // Controla a exibição da tabela de usuários
    const [showProducts, setShowProducts] = useState(false); // Controla a exibição da tabela de produtos
    const [loading, setLoading] = useState(false); // Estado para controlar o carregamento dos dados
    const [error, setError] = useState(null); // Estado para armazenar erros, se houver
    const [users, setUsers] = useState([]); // Estado para armazenar os usuários

    // Função para buscar usuários na API
    const fetchUsers = async () => {
        setLoading(true); // Inicia o estado de carregamento
        try {
            const response = await fetch('http://localhost:8080/auth/listarUsuarios');
            if (!response.ok) {
                throw new Error('Erro ao buscar usuários');
            }
            const data = await response.json();
            setUsers(data); // Armazena os usuários recebidos
        } catch (error) {
            setError('Erro ao buscar usuários.');
            console.error('Erro ao buscar usuários:', error);
        } finally {
            setLoading(false); // Termina o estado de carregamento
        }
    };

    // Função chamada ao clicar no botão "Listar Usuários"
    const handleListUsers = () => {
        setShowUsers(true); // Mostra a tabela de usuários
        setShowProducts(false); // Esconde a tabela de produtos
        fetchUsers(); // Busca os dados dos usuários
    };

    // Função chamada ao clicar no botão "Listar Produtos"
    const handleListProducts = () => {
        setShowProducts(true); // Mostra a tabela de produtos
        setShowUsers(false); // Esconde a tabela de usuários
    };

    return (
        <div className="home-back-office">
            {!showUsers && !showProducts ? (
                <>
                    <h1>Bem-vindo ao Back Office</h1>
                    <div>
                        <button 
                            type="button" 
                            className="btn btn-primary"
                            onClick={handleListProducts} // Chama a função para listar produtos
                        >
                            Listar Produtos
                        </button>
                        <button 
                            type="button" 
                            className="btn btn-secondary"
                            onClick={handleListUsers} // Chama a função para listar usuários
                        >
                            Listar Usuários
                        </button>
                    </div>
                </>
            ) : (
                <div>
                    {showUsers && (
                        <>
                            <h2>Lista de Usuários</h2>
                            {loading && <p>Carregando usuários...</p>}
                            {error && <p className="error">{error}</p>}
                            {!loading && !error && <UserTable users={users} />} {/* Passa a lista de usuários para UserTable */}
                        </>
                    )}
                    {showProducts && (
                        <>
                            <h2>Lista de Produtos</h2>
                            <ProductTable /> {/* Componente para exibir a tabela de produtos */}
                        </>
                    )}
                </div>
            )}
        </div>
    );
}

export default HomeBackOffice;
